import React, { useState } from "react";

const App = () => {
    let [num , setNum] = useState(5);
    const increment  = () => {
        setNum(num+1);
    }
    const decrement  = () => {
       if(num>0)
        setNum(num-1); 
        else
        setNum();
    }
  return (
  <>
      <div className="main_div">
          <div className="center_div">
          <h1>Incr Decr</h1>
          <div className="inner_div">
              <h2>{num}</h2>
              <button className="left" onClick={increment}>+</button>
              <button className="right" onClick={decrement}>-</button>
          </div>
          </div>
      </div>
  </>);
};
export default App;
