import React, { useState } from "react";

const App = () => {
    let [num , setNsum] = useState(5);
  return (
  <>
      <div className="main_div">
          <div className="center_div">
          <h1>Incr Decr</h1>
          <div className="inner_div">
              <h2>{num}</h2>
              <button className="left" onClick={increment}>+</button>
              <button className="right" onClick={decrement}>-</button>
          </div>
          </div>
      </div>
  </>);
};
export default App;
